import React, { useState } from 'react';
import axios from 'axios';
import './QueryPage.css';

const QueryPage = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!query.trim()) return;

        setLoading(true);
        setError('');
        setResults(null);

        try {
            const response = await axios.post('/query', { question: query });
            setResults(response.data);
        } catch (error) {
            setError(error.response?.data?.error || error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="query-page">
            <h2>Search Documents</h2>

            <form onSubmit={handleSubmit} className="query-form">
                <div className="query-input-group">
                    <input
                        type="text"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="Enter your question or search term..."
                        className="query-input"
                        disabled={loading}
                    />
                    <button type="submit" disabled={loading || !query.trim()} className="query-button">
                        {loading ? 'Searching...' : 'Search'}
                    </button>
                </div>
            </form>

            {loading && (
                <div className="loading-container">
                    <div className="spinner"></div>
                    <p>Searching through your documents...</p>
                </div>
            )}

            {error && (
                <div className="error-message">
                    ‚ùå Error: {error}
                </div>
            )}

            {results && (
                <div className="results-container">
                    <h3>Search Results</h3>
                    <p className="results-count">Found {results.count} matching document(s)</p>

                    {results.count > 0 ? (
                        <div className="results-grid">
                            {results.results.map((result, index) => (
                                <div key={index} className="result-card">
                                    <h4>{result.title}</h4>
                                    <p className="result-snippet">{result.snippet}</p>
                                    <div className="result-meta">
                                        <span>Document ID: {result.id}</span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    ) : (
                        <div className="no-results">
                            <p>No documents found matching your query.</p>
                            <p>Try different keywords or upload more documents.</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default QueryPage;
