import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import axios from 'axios';
import UploadPage from './components/UploadPage';
import QueryPage from './components/QueryPage';
import DocumentsPage from './components/DocumentsPage';
import './App.css';

function App() {
    const [serverStatus, setServerStatus] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        checkServerHealth();
    }, []);

    const checkServerHealth = async () => {
        try {
            const response = await axios.get('/health');
            setServerStatus(response.data);
        } catch (error) {
            console.error('Failed to connect to server:', error);
            setServerStatus({ status: 'ERROR', message: 'Server not reachable' });
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>PDF RAG Application</h1>
                    <div className="loading-spinner">Loading...</div>
                </header>
            </div>
        );
    }

    return (
        <Router>
            <div className="App">
                <header className="App-header">
                    <h1>PDF RAG Application</h1>
                    <nav className="nav-menu">
                        <NavLink to="/" className="nav-link" activeclassname="active">Upload</NavLink>
                        <NavLink to="/query" className="nav-link" activeclassname="active">Query</NavLink>
                        <NavLink to="/documents" className="nav-link" activeclassname="active">Documents</NavLink>
                    </nav>

                    <div className="status-bar">
                        <span className={`status-indicator ${serverStatus?.status === 'OK' ? 'success' : 'error'}`}>
                            Backend: {serverStatus?.status || 'Unknown'}
                        </span>
                        <span className={`status-indicator ${serverStatus?.neo4j === 'Connected' ? 'success' : 'error'}`}>
                            Neo4j: {serverStatus?.neo4j || 'Unknown'}
                        </span>
                    </div>
                </header>

                <main className="App-main">
                    <Routes>
                        <Route path="/" element={<UploadPage />} />
                        <Route path="/query" element={<QueryPage />} />
                        <Route path="/documents" element={<DocumentsPage />} />
                    </Routes>
                </main>
            </div>
        </Router>
    );
}

export default App;
