import React, { useState } from 'react';
import axios from 'axios';
import './QueryPage.css';

const QueryPage = () => {
    const [query, setQuery] = useState('');
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [queryHistory, setQueryHistory] = useState([]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!query.trim()) return;

        setLoading(true);
        setError('');
        setResult(null);

        try {
            const response = await axios.post('/query', { question: query });
            const newResult = {
                question: query,
                answer: response.data.answer,
                sources: response.data.sources || [],
                confidence: response.data.confidence || 0,
                timestamp: new Date().toLocaleString()
            };

            setResult(newResult);
            setQueryHistory(prev => [newResult, ...prev.slice(0, 9)]); // Keep last 10 queries

        } catch (error) {
            setError(error.response?.data?.error || error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleExampleQuery = (exampleQuery) => {
        setQuery(exampleQuery);
    };

    const clearHistory = () => {
        setQueryHistory([]);
    };

    return (
        <div className="query-page">
            <h2>Ask Questions About Your Documents</h2>

            <form onSubmit={handleSubmit} className="query-form">
                <div className="query-input-group">
                    <textarea
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        placeholder="Ask a question about your uploaded documents..."
                        className="query-input"
                        disabled={loading}
                        rows="3"
                    />
                    <button
                        type="submit"
                        disabled={loading || !query.trim()}
                        className="query-button"
                    >
                        {loading ? 'Thinking...' : 'Ask'}
                    </button>
                </div>
            </form>

            <div className="example-queries">
                <h3>Example Questions:</h3>
                <div className="example-buttons">
                    <button
                        onClick={() => handleExampleQuery('What is the main topic of the document?')}
                        className="example-button"
                    >
                        What is the main topic?
                    </button>
                    <button
                        onClick={() => handleExampleQuery('Who are the key people mentioned?')}
                        className="example-button"
                    >
                        Who are the key people?
                    </button>
                    <button
                        onClick={() => handleExampleQuery('What are the main conclusions?')}
                        className="example-button"
                    >
                        What are the conclusions?
                    </button>
                </div>
            </div>

            {loading && (
                <div className="loading-container">
                    <div className="spinner"></div>
                    <p>Analyzing your documents and generating response...</p>
                </div>
            )}

            {error && (
                <div className="error-message">
                    ‚ùå Error: {error}
                </div>
            )}

            {result && (
                <div className="result-container">
                    <div className="result-header">
                        <h3>Answer</h3>
                        <span className="confidence-badge">
                            Confidence: {(result.confidence * 100).toFixed(1)}%
                        </span>
                    </div>

                    <div className="answer-content">
                        <p>{result.answer}</p>
                    </div>

                    {result.sources.length > 0 && (
                        <div className="sources-section">
                            <h4>Sources:</h4>
                            <div className="sources-list">
                                {result.sources.map((source, index) => (
                                    <div key={index} className="source-item">
                                        <span className="source-title">{source.title}</span>
                                        <span className="source-similarity">
                                            {(source.similarity * 100).toFixed(1)}% match
                                        </span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            )}

            {queryHistory.length > 0 && (
                <div className="query-history">
                    <div className="history-header">
                        <h3>Query History</h3>
                        <button onClick={clearHistory} className="clear-history-btn">
                            Clear History
                        </button>
                    </div>

                    <div className="history-list">
                        {queryHistory.map((item, index) => (
                            <div key={index} className="history-item">
                                <div className="history-question">
                                    <strong>Q:</strong> {item.question}
                                </div>
                                <div className="history-answer">
                                    <strong>A:</strong> {item.answer.substring(0, 150)}...
                                </div>
                                <div className="history-meta">
                                    <span>{item.timestamp}</span>
                                    <span>Confidence: {(item.confidence * 100).toFixed(1)}%</span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default QueryPage;
