import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import './UploadPage.css';

const UploadPage = () => {
    const [uploadStatus, setUploadStatus] = useState('');
    const [uploading, setUploading] = useState(false);
    const [uploadedDocuments, setUploadedDocuments] = useState([]);
    const [uploadProgress, setUploadProgress] = useState(0);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        accept: {
            'application/pdf': ['.pdf']
        },
        multiple: false,
        maxSize: 100 * 1024 * 1024, // 100MB
        onDrop: handleFileUpload,
        onDropRejected: handleDropRejected
    });

    function handleDropRejected(rejectedFiles) {
        const errors = rejectedFiles[0].errors;
        if (errors.find(error => error.code === 'file-too-large')) {
            setUploadStatus('âŒ Error: File is too large. Maximum size is 100MB.');
        } else if (errors.find(error => error.code === 'file-invalid-type')) {
            setUploadStatus('âŒ Error: Only PDF files are allowed.');
        } else {
            setUploadStatus('âŒ Error: File upload rejected.');
        }
    }

    async function handleFileUpload(acceptedFiles) {
        if (acceptedFiles.length === 0) return;

        const file = acceptedFiles[0];
        const formData = new FormData();
        formData.append('pdf', file);

        setUploading(true);
        setUploadStatus('');
        setUploadProgress(0);

        try {
            const response = await axios.post('/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                },
                onUploadProgress: (progressEvent) => {
                    const percentCompleted = Math.round(
                        (progressEvent.loaded * 100) / progressEvent.total
                    );
                    setUploadProgress(percentCompleted);
                }
            });

            setUploadStatus(`âœ… Success: ${response.data.message}`);
            setUploadedDocuments(prev => [...prev, {
                id: response.data.docId,
                name: file.name,
                size: file.size,
                entitiesStored: response.data.entitiesStored,
                chunksStored: response.data.chunksStored,
                textLength: response.data.textLength,
                uploadTime: new Date().toLocaleString()
            }]);
        } catch (error) {
            console.error('Upload error:', error);
            setUploadStatus(`âŒ Error: ${error.response?.data?.error || error.message}`);
        } finally {
            setUploading(false);
            setUploadProgress(0);
        }
    }

    const formatFileSize = (bytes) => {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    };

    const clearUploadHistory = () => {
        setUploadedDocuments([]);
        setUploadStatus('');
    };

    return (
        <div className="upload-page">
            <h2>Upload PDF Documents</h2>
            <p className="upload-description">
                Upload your PDF documents to analyze them with AI. The system will extract text,
                identify entities, and create searchable embeddings for intelligent querying.
            </p>

            <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''} ${uploading ? 'uploading' : ''}`}>
                <input {...getInputProps()} />
                {uploading ? (
                    <div className="upload-progress">
                        <div className="progress-spinner">
                            <div className="spinner"></div>
                        </div>
                        <p>Processing PDF...</p>
                        <div className="progress-bar">
                            <div
                                className="progress-fill"
                                style={{ width: `${uploadProgress}%` }}
                            ></div>
                        </div>
                        <p className="progress-text">{uploadProgress}% uploaded</p>
                    </div>
                ) : (
                    <div className="dropzone-content">
                        <div className="upload-icon">ðŸ“„</div>
                        <h3>
                            {isDragActive
                                ? 'Drop the PDF here...'
                                : 'Drag & drop a PDF file here'}
                        </h3>
                        <p className="upload-hint">or click to select a file</p>
                        <div className="upload-requirements">
                            <p>â€¢ Only PDF files are supported</p>
                            <p>â€¢ Maximum file size: 100MB</p>
                            <p>â€¢ Text-based PDFs work best</p>
                        </div>
                    </div>
                )}
            </div>

            {uploadStatus && (
                <div className={`upload-status ${uploadStatus.includes('âœ…') ? 'success' : 'error'}`}>
                    {uploadStatus}
                </div>
            )}

            {uploadedDocuments.length > 0 && (
                <div className="uploaded-documents">
                    <div className="documents-header">
                        <h3>Recently Uploaded ({uploadedDocuments.length})</h3>
                        <button onClick={clearUploadHistory} className="clear-button">
                            Clear History
                        </button>
                    </div>
                    <div className="documents-grid">
                        {uploadedDocuments.map((doc, index) => (
                            <div key={index} className="document-card">
                                <div className="document-icon">ðŸ“„</div>
                                <h4>{doc.name}</h4>
                                <div className="document-stats">
                                    <div className="stat">
                                        <span className="stat-label">Size:</span>
                                        <span className="stat-value">{formatFileSize(doc.size)}</span>
                                    </div>
                                    <div className="stat">
                                        <span className="stat-label">Entities:</span>
                                        <span className="stat-value">{doc.entitiesStored}</span>
                                    </div>
                                    <div className="stat">
                                        <span className="stat-label">Chunks:</span>
                                        <span className="stat-value">{doc.chunksStored}</span>
                                    </div>
                                    <div className="stat">
                                        <span className="stat-label">Text Length:</span>
                                        <span className="stat-value">{doc.textLength?.toLocaleString()} chars</span>
                                    </div>
                                </div>
                                <p className="upload-time">Uploaded: {doc.uploadTime}</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            <div className="upload-tips">
                <h3>Tips for Best Results:</h3>
                <ul>
                    <li>Use text-based PDFs for optimal text extraction</li>
                    <li>Ensure PDFs are not password-protected</li>
                    <li>For large documents, processing may take a few minutes</li>
                    <li>The system works best with well-formatted documents</li>
                </ul>
            </div>
        </div>
    );
};

export default UploadPage;
