import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DocumentsPage.css';

const DocumentsPage = () => {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchDocuments();
    }, []);

    const fetchDocuments = async () => {
        try {
            const response = await axios.get('/documents');
            setDocuments(response.data.documents || []);
        } catch (error) {
            setError('Failed to load documents');
        } finally {
            setLoading(false);
        }
    };

    const clearAllDocuments = async () => {
        if (!window.confirm('Are you sure you want to delete all documents? This action cannot be undone.')) {
            return;
        }

        try {
            await axios.delete('/documents');
            setDocuments([]);
            alert('All documents cleared successfully');
        } catch (error) {
            alert('Failed to clear documents: ' + error.message);
        }
    };

    if (loading) {
        return (
            <div className="documents-page">
                <div className="loading-container">
                    <div className="spinner"></div>
                    <p>Loading documents...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="documents-page">
            <div className="documents-header">
                <h2>Document Library</h2>
                {documents.length > 0 && (
                    <button onClick={clearAllDocuments} className="clear-button">
                        Clear All Documents
                    </button>
                )}
            </div>

            {error && (
                <div className="error-message">
                    ‚ùå Error: {error}
                </div>
            )}

            {documents.length === 0 ? (
                <div className="empty-state">
                    <div className="empty-icon">üìÇ</div>
                    <h3>No documents uploaded yet</h3>
                    <p>Upload your first PDF document to get started!</p>
                </div>
            ) : (
                <div className="documents-grid">
                    {documents.map((doc) => (
                        <div key={doc.id} className="document-card">
                            <div className="document-icon">üìÑ</div>
                            <h3>{doc.title}</h3>
                            <p className="document-id">ID: {doc.id}</p>
                            <p className="upload-date">
                                Uploaded: {new Date(doc.uploadDate).toLocaleDateString()}
                            </p>
                        </div>
                    ))}
                </div>
            )}

            <div className="documents-stats">
                <p>Total Documents: {documents.length}</p>
            </div>
        </div>
    );
};

export default DocumentsPage;
